import React, { useState } from 'react';
import axios from 'axios';

const surveyQuestions = {
  student: [
    { question: "What are your favorite subjects?", options: ["Math", "Science", "Literature", "Art"] }
  ],
  trader: [
    { question: "Which markets do you trade in?", options: ["Stocks", "Forex", "Crypto", "Commodities"] }
  ],
  bankEmployee: [
    { question: "What types of banking services do you handle?", options: ["Loans", "Savings", "Investments", "Customer Service"] }
  ]
};

function SurveyForm() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    ageGroup: '',
    section: '',
    answers: {}
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/submit', formData);
      alert('Survey submitted!');
      console.log('Survey submission response:', response.data);
    } catch (error) {
      console.error('Error submitting survey:', error);
      alert('There was an error submitting your survey. Please try again.');
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleMultiSelect = (question, option) => {
    let newAnswers = { ...formData.answers };
    if (!newAnswers[question]) {
      newAnswers[question] = [];
    }
    if (newAnswers[question].includes(option)) {
      newAnswers[question] = newAnswers[question].filter(opt => opt !== option);
    } else {
      newAnswers[question].push(option);
    }
    setFormData({ ...formData, answers: newAnswers });
  };

  const renderQuestions = () => {
    if (!formData.section) return null;
    return surveyQuestions[formData.section].map((q, index) => (
      <div key={index}>
        <h4>{q.question}</h4>
        {q.options.map(option => (
          <label key={option}>
            <input 
              type="checkbox" 
              onChange={() => handleMultiSelect(q.question, option)}
              checked={formData.answers[q.question] && formData.answers[q.question].includes(option)}
            /> {option}
          </label>
        ))}
      </div>
    ));
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" name="name" placeholder="Name" onChange={handleChange} required />
      <input type="email" name="email" placeholder="Email" onChange={handleChange} required />
      <select name="ageGroup" onChange={handleChange} required>
        <option value="">Select Age Group</option>
        <option value="18-25">18-25</option>
        <option value="26-35">26-35</option>
        <option value="36-50">36-50</option>
        <option value="50+">50+</option>
      </select>
      <select name="section" onChange={handleChange} required>
        <option value="">Select Section</option>
        <option value="student">Student</option>
        <option value="trader">Trader</option>
        <option value="bankEmployee">Bank Employee</option>
      </select>
      {renderQuestions()}
      <button type="submit">Submit</button>
    </form>
  );
}

export default SurveyForm;
