import React, { useState } from 'react';
import axios from 'axios';

const surveyQuestions = {
  student: [
    { question: "What are your favorite subjects?", options: ["Math", "Science", "Literature", "Art"] },
    { question: "Preferred learning environment?", options: ["In-person", "Online", "Hybrid"] },
    { question: "Extracurricular interests?", options: ["Sports", "Music", "Art", "Tech"] },
    { question: "What's most challenging in your studies?", options: ["Time management", "Content", "Exams", "Motivation"] },
    { question: "How do you prepare for exams?", options: ["Solo study", "Group study", "Online resources", "Tutoring"] },
    { question: "What motivates you to learn?", options: ["Career goals", "Interest in subject", "Grades", "Personal growth"] },
    { question: "Do you prefer theoretical or practical learning?", options: ["Theoretical", "Practical"] },
    { question: "How often do you seek help from professors?", options: ["Often", "Sometimes", "Rarely", "Never"] },
    { question: "What resources do you use for studying?", options: ["Textbooks", "Internet", "Library", "Peers"] },
    { question: "Are you satisfied with your academic program?", options: ["Yes", "No", "Somewhat"] }
  ],
  trader: [
    { question: "Which trading strategies do you employ?", options: ["Day trading", "Swing trading", "Long-term investment", "Scalping"] },
    { question: "Preferred markets?", options: ["Stocks", "Forex", "Cryptocurrency", "Commodities"] },
    { question: "How do you manage risk?", options: ["Stop loss", "Diversification", "Hedging", "Position sizing"] },
    { question: "What trading platforms do you use?", options: ["MetaTrader", "Robinhood", "Binance", "Interactive Brokers"] },
    { question: "Do you use technical analysis?", options: ["Yes", "No"] },
    { question: "How do you stay informed?", options: ["News", "Blogs", "Social media", "Broker research"] },
    { question: "Do you trade full-time or part-time?", options: ["Full-time", "Part-time"] },
    { question: "What's your approach to market volatility?", options: ["Embrace", "Avoid", "Adapt"] },
    { question: "How do you handle trading losses?", options: ["Review strategy", "Take a break", "Ignore", "Learn from it"] },
    { question: "Is trading your primary income source?", options: ["Yes", "No"] }
  ],
  bankEmployee: [
    { question: "What banking services do you primarily handle?", options: ["Loans", "Savings", "Investments", "Customer service"] },
    { question: "How do you manage customer complaints?", options: ["Listen and resolve", "Escalate", "Apologize", "Offer solutions"] },
    { question: "What tools do you use for your job?", options: ["CRM software", "Excel", "Banking software", "Email"] },
    { question: "How often do you deal with compliance issues?", options: ["Daily", "Weekly", "Monthly", "Rarely"] },
    { question: "What motivates you at work?", options: ["Customer satisfaction", "Job security", "Career growth", "Teamwork"] },
    { question: "Do you prefer working with individuals or businesses?", options: ["Individuals", "Businesses"] },
    { question: "How do you keep up with banking regulations?", options: ["Seminars", "Online courses", "News", "Internal training"] },
    { question: "What's the most challenging part of your job?", options: ["Regulatory compliance", "Technology", "Customer service", "Sales targets"] },
    { question: "Are you involved in digital banking initiatives?", options: ["Yes", "No"] },
    { question: "How do you handle stress at work?", options: ["Breaks", "Meditation", "Exercise", "Support from colleagues"] }
  ]
};

function SurveyForm() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    ageGroup: '',
    section: '',
    answers: {}
  });
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (isSubmitting) return;

    setIsSubmitting(true);
    try {
      await axios.post('http://localhost:5000/api/submit', formData);
      alert('Survey submitted successfully!');
      // Optionally, reset the form if needed
      setFormData({
        name: '',
        email: '',
        ageGroup: '',
        section: '',
        answers: {}
      });
    } catch (error) {
      if (error.response && error.response.status !== 201) {
        console.error('Error submitting survey:', error);
        alert('There was an error submitting your survey. Please try again.');
      } else {
        alert('Survey submitted, but there was an issue processing the response.');
      }
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleMultiSelect = (question, option) => {
    let newAnswers = { ...formData.answers };
    if (!newAnswers[question]) {
      newAnswers[question] = [];
    }
    if (newAnswers[question].includes(option)) {
      newAnswers[question] = newAnswers[question].filter(opt => opt !== option);
    } else {
      newAnswers[question].push(option);
    }
    setFormData({ ...formData, answers: newAnswers });
  };

  const renderQuestions = () => {
    if (!formData.section) return null;
    return surveyQuestions[formData.section].map((q, index) => (
      <div key={index}>
        <h4>{q.question}</h4>
        {q.options.map(option => (
          <label key={option}>
            <input 
              type="checkbox" 
              onChange={() => handleMultiSelect(q.question, option)}
              checked={formData.answers[q.question]?.includes(option) || false}
            /> <span>{option}</span>
          </label>
        ))}
      </div>
    ));
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" name="name" placeholder="Name" onChange={handleChange} value={formData.name} required disabled={isSubmitting} />
      <input type="email" name="email" placeholder="Email" onChange={handleChange} value={formData.email} required disabled={isSubmitting} />
      <select name="ageGroup" onChange={handleChange} value={formData.ageGroup} required disabled={isSubmitting}>
        <option value="">Select Age Group</option>
        <option value="18-25">18-25</option>
        <option value="26-35">26-35</option>
        <option value="36-50">36-50</option>
        <option value="50+">50+</option>
      </select>
      <select name="section" onChange={handleChange} value={formData.section} required disabled={isSubmitting}>
        <option value="">Select Section</option>
        <option value="student">Student</option>
        <option value="trader">Trader</option>
        <option value="bankEmployee">Bank Employee</option>
      </select>
      {renderQuestions()}
      <button type="submit" disabled={isSubmitting}>
        {isSubmitting ? 'Submitting...' : 'Submit'}
      </button>
    </form>
  );
}

export default SurveyForm;
